import axios from 'axios';
import React, { Component } from 'react';
import TextInput from '../elements/TextInput';

// import style from '../style.css';

export default class Card extends Component {
    constructor(props) {
        super(props);
        this.state = {
            posts: [],
            visible: false,
            link: "",
            nama: "",
            deskripsi: "",
            harga: "",
        };
    }
    handleButton = () => {
        this.setState({
            visible: true,
        });
    };
    handleLink = (e) => {
        this.setState({
            link: e.target.value,
        });
        console.log(this.state.link);
    };
    handleNama = (e) => {
        this.setState({
            nama: e.target.value,
        });
        console.log(this.state.nama);
    };
    handleDeskripsi = (e) => {
        this.setState({
            date: e.target.value,
        });
        console.log(this.state.deskripsi);
    };
    handleHarga = (e) => {
        this.setState({
            harga: e.target.value,
        });
        console.log(this.state.harga);
    };
    componentDidMount() {
        axios({
            method: "get",
            url: "https://jualarduino.herokuapp.com/posts",
            headers: {
                accept: "*/*",
            },
        })
            .then((data) => {
                console.log(data.data);
                this.setState({
                    posts: data.data,
                });
            })
            .catch((error) => {
                console.log(error);
            });
    }
    render() {
        return (
            <div>

            </div>
        );
    }
}
