import styled, { css } from 'styled-components';

export default styled.input`
  background: transparent;
  border-radius: 15px;
  border: 2.5px solid grey;
  color: white;
  margin: 5px 5px;
  padding: 7px 13px;
  width:90px;
  ${(props) => props.border && css`
    background: white;
    color: #001529;
    &:hover{
      color: #001529;
      border-color: white;
      font-weight: 500;
    }
  `}
`;
