import { Tooltip } from 'antd';
import axios from 'axios';
import React, { Component } from 'react';
import Button from '../elements/Button';
import TextInput from '../elements/TextInput';

//funciton components
function Square(props) {
    return (
        <div>
            <div className="Box">
                <div className="Container">
                    <img alt="img" src={props.link} />
                </div>

                <div className="Container">
                    <h1>{props.nama}</h1>
                    <hr></hr>
                    {/* <h3>Deskripsi</h3>
                    <p>{props.deskripsi}</p> */}
                    <h3>{props.harga}</h3>
                    <Button onClick="this">View More</Button>
                </div>
            </div>
        </div>
    );
}

//export card
export default class Card extends Component {
    constructor(props) {
        super(props);
        this.state = {
            posts: [],
            visible: false,
            link: "",
            nama: "",
            deskripsi: "",
            harga: "",
        };
    }
    handleButton = () => {
        this.setState({
            visible: true,
        });
    };
    handleLink = (e) => {
        this.setState({
            link: e.target.value,
        });
        console.log(this.state.link);
    };
    handleNama = (e) => {
        this.setState({
            nama: e.target.value,
        });
        console.log(this.state.nama);
    };
    handleDeskripsi = (e) => {
        this.setState({
            date: e.target.value,
        });
        console.log(this.state.deskripsi);
    };
    handleHarga = (e) => {
        this.setState({
            harga: e.target.value,
        });
        console.log(this.state.harga);
    };
    handleSearch = (e) => {
        this.setState({
            search: e.target.value,
        });
        console.log(this.state.search);
    }
    componentDidMount() {
        axios({
            method: "get",
            url: "https://jualarduino.herokuapp.com/posts",
            headers: {
                accept: "*/*",
            },
        })
            .then((data) => {
                console.log(data.data);
                this.setState({
                    posts: data.data,
                });
            })
            .catch((error) => {
                console.log(error);
            });
    }
    render() {
        return (
            <div>
                <div className="container">
                    <div className="flex">
                        <Tooltip placement="topLeft" title="Case Sensitive. Cari dengan huruf kapital di awal kata.">
                            <TextInput type="text" id="inputText" placeholder="Search Arduino..." onChange={this.handleSearch} value={this.state.search} />
                        </Tooltip>
                        <Button style={{ width: "90px" }} onClick={this.handleSearch}>Search</Button>
                    </div>
                    <div className="flex">
                        <br></br>
                        <h2 style={{ textAlign: "left" }}>Arduino Products</h2>
                        <hr></hr>
                    </div>
                    <div className="container">
                        {this.state.posts
                            .filter(user => user.nama.includes(this.state.search))
                            .map(user => {
                                return (
                                    <div className="card" key={user.nama}>
                                        <div className="card-body">
                                            <h5 className="card-title">Nama : {user.nama}</h5>
                                            <h6 className="card-subtitle mb-2 text-muted">
                                                Nim : {user.harga}
                                            </h6>
                                            <p className="card-text">Asal : {user.link}</p>
                                        </div>
                                    </div>
                                );
                            })}
                    </div>
                </div>
            </div>
        );
    }
}
